import { MAT_DIALOG_DATA } from '@angular/material/dialog';
import { EmployeeService } from '../services/employee.service';
import { Employee } from './../model/employee';
import { Component, Inject, Input } from '@angular/core';
import { Unsub } from '../unsub';
import { takeUntil } from 'rxjs';

@Component({
  selector: 'app-employee-form-dialog',
  templateUrl: './employee-form-dialog.component.html',
  styleUrls: ['./employee-form-dialog.component.css']
})
export class EmployeeFormDialogComponent extends Unsub {
  employee: Employee = new Employee();
  id: number;


  constructor(private employeeService: EmployeeService, @Inject(MAT_DIALOG_DATA) public data: {}) {
    super();
  }


  ngOnInit(): void {
    if (this.data['typeOperation'] === "Update" || this.data['typeOperation'] === "details") {
      this.id = this.data['id'];
      this.employeeService.getEmployeeById(this.id).pipe(takeUntil(this.unsbscribe)).subscribe(emp => { this.employee = emp; });
    }
  }
}
<div class="modal " mat-dialog-content>

  <div class="row">
    <div class="card col-md-6 offset-md-3 offset-md-3">
      <div class="row">
        <h3 class="text-center title"> {{data['typeOperation']}} Employee </h3>
        <hr />
        <div class="card-body">
          <form>

            <div class="form-group">
              <label> First Name</label>
              <input [disabled]="data['typeOperation']==='details'" type="text" class="form-control" id="firstName"
                [(ngModel)]="employee.firstName" name="firstName">
            </div>

            <div class="form-group">
              <label> Last Name</label>
              <input [disabled]="data['typeOperation']==='details'" type="text" class="form-control" id="lastName"
                [(ngModel)]="employee.lastName" name="lastName">
            </div>

            <div class="form-group">
              <label> Email Id</label>
              <input [disabled]="data['typeOperation']==='details'" type="text" class="form-control" id="emailId"
                [(ngModel)]="employee.emailId" name="emailId">
            </div>

            <br />
            <button *ngIf="data['typeOperation']!='details'" class="btn btn-success" [mat-dialog-close]="employee"
              type="submit">{{data['typeOperation']}}</button>
            <button class="btn " mat-dialog-close>cancel</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
