import { MAT_DIALOG_DATA } from '@angular/material/dialog';
import { EmployeeService } from '../services/employee.service';
import { Employee } from './../model/employee';
import { Component, Inject, Input } from '@angular/core';

@Component({
  selector: 'app-employee-form-dialog',
  templateUrl: './employee-form-dialog.component.html',
  styleUrls: ['./employee-form-dialog.component.css']
})
export class EmployeeFormDialogComponent {
  employee: Employee = new Employee();
  id: number;


  constructor(private employeeService: EmployeeService, @Inject(MAT_DIALOG_DATA) public data: {}) { }


  ngOnInit(): void {
    if (this.data['typeOperation'] === "Update" || this.data['typeOperation'] === "detail") {
      this.id = this.data['id'];
      this.employeeService.getEmployeeById(this.id).subscribe(emp => { this.employee = emp; });
    }
  }
}
